document.addEventListener("DOMContentLoaded",function(){var e=document.getElementById("hamburgerIcon");const t=document.querySelector(".header__menu-wrapper"),n=document.querySelector(".header__menu-close");e.addEventListener("click",()=>{t.classList.toggle("show-menu"),n.classList.toggle("active")}),n.addEventListener("click",()=>{t.classList.toggle("show-menu"),n.classList.toggle("active")})});